import React, { useState, useEffect } from 'react';

function App() {
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState({
    name: '',
    professor: '',
    syllabus: null
  });

  useEffect(() => {
    // Fetch courses when the component mounts
    fetchCourses();
  }, []);

  // Fetch courses from the backend
  const fetchCourses = async () => {
    try {
      const response = await fetch('http://localhost:3002/courses');
      const data = await response.json();
      setCourses(data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  // Handle input changes in the form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCourse({
      ...newCourse,
      [name]: value
    });
  };

  // Handle file input
  const handleFileChange = (e) => {
    setNewCourse({
      ...newCourse,
      syllabus: e.target.files[0]
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', newCourse.name);
    formData.append('professor', newCourse.professor);
    formData.append('syllabus', newCourse.syllabus);

    try {
      const response = await fetch('http://localhost:3002/courses', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (data.course) {
        setCourses([...courses, data.course]);
        setNewCourse({ name: '', professor: '', syllabus: null });
      }
    } catch (error) {
      console.error('Error adding course:', error);
    }
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>Welcome to the Course Repository</h1>

          {/* Home Screen Routing */}
          <Switch>
            <Route exact path="/" component={Home} />

            {/* Courses Page Route */}
            <Route path="/courses">
              <h2>Course List</h2>

              {courses.length === 0 ? (
                <p>No courses available</p>
              ) : (
                <ul>
                  {courses.map((course) => (
                    <li key={course.id}>
                      {course.name} - {course.professor}
                      <button>Delete</button>
                    </li>
                  ))}
                </ul>
              )}

              <h3>Add a New Course</h3>
              <form onSubmit={handleSubmit}>
                <input
                  type="text"
                  name="name"
                  placeholder="Course Name"
                  value={newCourse.name}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="text"
                  name="professor"
                  placeholder="Professor"
                  value={newCourse.professor}
                  onChange={handleInputChange}
                  required
                />
                <label className="file-upload">
                  Upload Syllabus
                  <input
                    type="file"
                    name="syllabus"
                    onChange={handleFileChange}
                    required
                  />
                </label>
                <button type="submit">Add Course</button>
              </form>
            </Route>
          </Switch>
        </header>
      </div>
    </Router>
  );
}

export default App;
