// src/Home.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedFilter, setSelectedFilter] = useState('');
  const [courses, setCourses] = useState([
    { id: 1, name: 'Data Structures', professor: 'Dr. Smith', keywords: ['algorithms', 'C++'] },
    { id: 2, name: 'Machine Learning', professor: 'Dr. Johnson', keywords: ['AI', 'Python'] },
    { id: 3, name: 'Web Development', professor: 'Prof. Lee', keywords: ['React', 'JavaScript'] }
  ]);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleFilterChange = (e) => {
    setSelectedFilter(e.target.value);
  };

  const navigateToCourses = () => {
    navigate('/courses');
  };

  const filteredCourses = courses.filter(course =>
    course.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    course.keywords.some(keyword => keyword.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  return (
    <div className="home-container">
      <header>
        <h1>Welcome to the Course Repository</h1>
        <p>
          Explore and manage courses efficiently. Upload syllabi, filter by keywords, and stay organized.
        </p>
        <a href="https://github.com/your-repo-link" target="_blank" rel="noopener noreferrer">
          <button>Go to Repo</button>
        </a>
      </header>

      <div className="search-bar">
        <input
          type="text"
          value={searchQuery}
          onChange={handleSearchChange}
          placeholder="Search for a course or keyword"
        />
      </div>

      <div className="filter-section">
        <label>Filter by Category: </label>
        <select value={selectedFilter} onChange={handleFilterChange}>
          <option value="">All</option>
          <option value="AI">AI</option>
          <option value="C++">C++</option>
          <option value="JavaScript">JavaScript</option>
        </select>
      </div>

      <div className="file-upload">
        <label className="upload-label">
          Upload File
          <input type="file" />
        </label>
      </div>

      <div className="courses-list">
        {filteredCourses.map(course => (
          <div key={course.id} className="course-card">
            <h3>{course.name}</h3>
            <p>{course.professor}</p>
            <p><strong>Keywords:</strong> {course.keywords.join(', ')}</p>
          </div>
        ))}
      </div>

      <div className="navigate-button" onClick={navigateToCourses}>
        <button>View Courses</button>
      </div>
    </div>
  );
};

export default Home;
