import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // Replace Switch with Routes
import Home from './home'; // Import Home component

function App() {
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState({
    name: '',
    professor: '',
    syllabus: null
  });
  const [filter, setFilter] = useState(''); // Add filter state

  useEffect(() => {
    // Fetch courses when the component mounts
    fetchCourses();
  }, []);

  // Fetch courses from the backend
  const fetchCourses = async () => {
    try {
      const response = await fetch('http://localhost:3002/courses');
      const data = await response.json();
      setCourses(data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  // Handle input changes in the form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCourse({
      ...newCourse,
      [name]: value
    });
  };

  // Handle file input
  const handleFileChange = (e) => {
    setNewCourse({
      ...newCourse,
      syllabus: e.target.files[0]
    });
  };

  // Handle filter change
  const handleFilterChange = (e) => {
    setFilter(e.target.value);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', newCourse.name);
    formData.append('professor', newCourse.professor);
    formData.append('syllabus', newCourse.syllabus);

    try {
      const response = await fetch('http://localhost:3002/courses', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (data.course) {
        setCourses([...courses, data.course]);
        setNewCourse({ name: '', professor: '', syllabus: null });
      }
    } catch (error) {
      console.error('Error adding course:', error);
    }
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>Welcome to the Course Repository</h1>

          {/* Home Screen Routing */}
          <Routes> {/* Replace Switch with Routes */}
            <Route path="/" element={<Home />} /> {/* Update Route syntax */}

            {/* Courses Page Route */}
            <Route
              path="/courses"
              element={
                <>
                  <div className="filter-dropdown">
                    <label htmlFor="filter">Filter Courses:</label>
                    <select id="filter" value={filter} onChange={handleFilterChange}>
                      <option value="">All</option>
                      <option value="professor">By Professor</option>
                      <option value="syllabus">With Syllabus</option>
                    </select>
                  </div>

                  <h2>Course List</h2>

                  <h3>Add a New Course</h3>
                  <form onSubmit={handleSubmit}>
                    <input
                      type="text"
                      name="name"
                      placeholder="Course Name"
                      value={newCourse.name}
                      onChange={handleInputChange}
                      required
                    />
                    <input
                      type="text"
                      name="professor"
                      placeholder="Professor"
                      value={newCourse.professor}
                      onChange={handleInputChange}
                      required
                    />
                    <label className="file-upload">
                      <input
                        type="file"
                        name="syllabus"
                        onChange={handleFileChange}
                        required
                      />
                    </label>
                    <button type="submit">Add Course</button>
                  </form>

                  <div className="course-cards">
                    {courses
                      .filter((course) => {
                        if (filter === 'professor') {
                          return course.professor;
                        }
                        if (filter === 'syllabus') {
                          return course.syllabus;
                        }
                        return true;
                      })
                      .map((course) => (
                        <div key={course.id} className="course-card">
                          <h3>{course.name}</h3>
                          <p>{course.professor}</p>
                          <p>Syllabus: {course.syllabus ? "Uploaded" : "Not Uploaded"}</p>
                          <button>Delete</button>
                        </div>
                      ))}
                  </div>
                </>
              }
            />
          </Routes>
        </header>
      </div>
    </Router>
  );
}

export default App;
