import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import Home from './home';
import CourseDetails from './courseDetails'; // New component for course-specific uploads

function App() {
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState({
    name: '',
    professor: '',
    syllabus: null,
  });

  const navigate = useNavigate(); // Define navigate using useNavigate

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await fetch('http://localhost:3002/courses');
      const data = await response.json();
      setCourses(data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCourse({ ...newCourse, [name]: value });
  };

  const handleFileChange = (e) => {
    setNewCourse({ ...newCourse, syllabus: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', newCourse.name);
    formData.append('professor', newCourse.professor);
    formData.append('syllabus', newCourse.syllabus);

    try {
      const response = await fetch('http://localhost:3002/courses', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (data.course) {
        setCourses([...courses, data.course]);
        setNewCourse({ name: '', professor: '', syllabus: null });
      }
    } catch (error) {
      console.error('Error adding course:', error);
    }
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>Welcome to the Course Repository</h1>
        </header>
        <div className="main-container">
          <aside className="sidebar">
            <h3>Filter/Search</h3>
            <input type="text" placeholder="Search courses..." />
          </aside>
          <main className="content">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route
                path="/courses"
                element={
                  <>
                    <h2>Course Listings</h2>
                    <div className="course-cards">
                      {courses.length === 0 ? (
                        <p>No courses available. Add a new course below.</p>
                      ) : (
                        courses.map((course) => (
                          <div
                            key={course.id}
                            className="course-card"
                            onClick={() => navigate(`/courses/${course.id}`)} // Use navigate here
                          >
                            <h3>{course.name}</h3>
                            <p>Professor: {course.professor}</p>
                          </div>
                        ))
                      )}
                    </div>
                    <h3>Upload Section</h3>
                    <form onSubmit={handleSubmit}>
                      <input
                        type="text"
                        name="name"
                        placeholder="Course Name"
                        value={newCourse.name}
                        onChange={handleInputChange}
                        required
                      />
                      <input
                        type="text"
                        name="professor"
                        placeholder="Professor"
                        value={newCourse.professor}
                        onChange={handleInputChange}
                        required
                      />
                      <label>
                        <input
                          type="file"
                          name="syllabus"
                          onChange={handleFileChange}
                          required
                        />
                      </label>
                      <button type="submit">Add Course</button>
                    </form>
                  </>
                }
              />
              <Route path="/courses/:id" element={<CourseDetails />} />
            </Routes>
          </main>
        </div>
      </div>
    </Router>
  );
}

export default App;
